import React from "react";
import { useLoaderData, redirect } from "react-router-dom";
import axios from "axios";

export default function CmessageDisplay() {
  const clientMsg = useLoaderData();
  return (
    <div>
      {clientMsg?.clientMessages?.map((item) => (
        <div>
          <p style={{ color: "whitesmoke", backgroundColor: "black" }}>
            {item.msgContent}
          </p>
        </div>
      ))}
      {clientMsg?.lancerMessages}
    </div>
  );
}

export async function Loader({ params }) {
  const response = await axios.get(
    `http://localhost/5500/home/${params.userId}/tasks/${params.fUser}/messages`
  );
  if (response.data) return response.data;
  else return redirect(`/home/${params.userId}/tasks/${params.fUser}/messages`);
}
