import React from "react";
import { useLoaderData } from "react-router-dom";
import axios from "axios";

export default function CmessageDisplay() {
    const params = useParams();
  const clientMsg = useLoaderData();
  const [allMessages, setAllMessages] = React.useState("");
  React.useEffect(() => {
    axios
      .get(
        `http://localhost:5500/home/${params.userId}/tasks/${params.fUser}/messages`
      )
      .then((res) => res)
      .then((data) => setAllMessages(data.data));
  });
  return (
    <div>
      <h2>messages goes here</h2>
      {clientMsg?.clientMessages?.map((item) => (
        <div>
          <span
            style={{
              color: "whitesmoke",
              backgroundColor: "black",
              width: "8rem",
              margin: "1rem",
              borderRadius: "6px",
              display: "block",
            }}
          >
            {item.msgContent}
          </span>
        </div>
      ))}
      {clientMsg?.lancerMessages?.map((item) => (
        <span
          style={{
            color: "blueViolet",
            backgroundColor: "whitesmoke",
            width: "8rem",
            margin: "1rem",
            borderRadius: "6px",
            display: "block",
          }}
        >
          {item.msgContent}
        </span>
      ))}
    </div>
  );
}

