import React from "react";
import { useOutletContext, Form, Link, redirect } from "react-router-dom";
import axios from "axios";

export default function MainPage() {
  const userData = useOutletContext();
  return (
    <div className="MainPage">
      <div className="searchBar">
        <fieldset>
          <input type="search" placeholder="Search skills" />
          <button>Search</button>
        </fieldset>
      </div>

      <div className="freelancersDisplay">
        {userData.freelancer?.map((item) => (
          <div className="freelancerData">
            <div className="lancerDetails">
              <img alt="" />
              <h3>{item.UserName}</h3>
              <h3>{item.Rating}</h3>
              <h3>{item.Skill}</h3>
            </div>

            <Link to={`${item.UserName}`}>view profile &rarr;</Link>
            <Form method="post">
              <input type="text" value={item.UserName} name="lancerId" />
              <input
                type="text"
                value={userData.user.UserName}
                name="clientId"
              />
              <button type="submit">Request</button>
            </Form>
          </div>
        ))}
      </div>
    </div>
  );
}

export async function Action({ params, request }) {
  console.log(params.userId);
  const formData = Object.fromEntries(await request.formData());
  const res = await axios.post(
    `http://localhost:5500/home/${params.userId}`,
    formData
  );
  if (res.data) return redirect(`/home/${params.userId}/tasks`);
}
