import React from "react";
import { useLoaderData, useParams } from "react-router-dom";
import axios from "axios";

export default function FMessageDisplay() {
  const messages = useLoaderData();
  return (
    <div className="messageDisplay">
      <h1>Messages goes here</h1>
      {setInterval(allMessages,5000,messages)}
    </div>
  );
}

function allMessages(messages) {
  {
    messages?.lancerMessages?.map((item) => {
      return (
        <span
          style={{
            color: "whitesmoke",
            backgroundColor: "black",
            width: "8rem",
            margin: "1rem",
            borderRadius: "6px",
            display: "block",
          }}
        >
          {item.msgContent}
        </span>
      );
    });
  }
  {
    messages?.clientMessages?.map((item) => {
      return (
        <span
          style={{
            color: "blueViolet",
            backgroundColor: "whitesmoke",
            width: "8rem",
            margin: "1rem",
            borderRadius: "6px",
            display: "block",
          }}
        >
          {item.msgContent}
        </span>
      );
    });
  }
}

export async function Loader({ params }) {
  const response = await axios.get(
    `http://localhost:5500/freelancer/${params.fUser}/tasks/${params.userId}/messages`
  );
  if (response.data) {
    return response.data;
  }
}
